    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed

        Aluno aluno = new Aluno();
        AlunoDao alun = new AlunoDao();
        Utilitario util = new Utilitario();

        aluno.setIdAluno(Integer.parseInt(this.Jt_ConsulAluno.getText()));

        aluno.setNome_aluno(this.jT_Nome_aluno.getText());
        aluno.setSobrenome_aluno(this.Jt_sobrenome_aluno.getText());
        aluno.setRg_aluno(Integer.parseInt(this.Jt_rg_aluno.getText()));
        aluno.setCpf_aluno(Double.parseDouble(this.Jt_cpf_aluno.getText()));
        aluno.setTelefone_aluno(Double.parseDouble(this.Jt_telefone_aluno.getText()));
        aluno.setEndereco_aluno(this.Jt_endereco_aluno.getText());
        aluno.setEmail_aluno(this.Jt_email_aluno.getText());
        aluno.setNascimento_aluno(new java.sql.Date(jNascimento.getDate().getTime()));
        aluno.setCadastro_aluno(new java.sql.Date(jCadastro.getDate().getTime()));

        int resposta = JOptionPane.showConfirmDialog(null, "Você tem certeza que quer alterar este registro? Está ação não poderá ser revertida.");

        if (resposta == JOptionPane.YES_OPTION) {
            alun.alterar(aluno);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

