    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed

        Aluno aluno = new Aluno();
        AlunoDao alun = new AlunoDao();

        aluno.setIdAluno(Integer.parseInt(this.Jt_ConsulAluno.getText()));
        aluno = alun.consultar(aluno);

        jT_Nome_aluno.setText(aluno.getNome_aluno());
        Jt_sobrenome_aluno.setText(aluno.getSobrenome_aluno());
        Jt_rg_aluno.setInt(aluno.getRg_aluno());
        Jt_cpf_aluno.setDouble(aluno.getCpf_aluno());
        Jt_endereco_aluno.setText(aluno.getEndereco_aluno());
        Jt_telefone_aluno.setDouble(aluno.getTelefone_aluno());
        Jt_email_aluno.setText(aluno.getEmail_aluno());
        jNascimento.setDate(aluno.getNascimento_aluno());
        jCadastro.setDate(aluno.getCadastro_aluno());

        alun.consultar(aluno);
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed

        Aluno aluno = new Aluno();
        AlunoDao alun = new AlunoDao();

        int resposta;
        resposta = JOptionPane.showConfirmDialog(null, "Você tem certeza que quer apagar este Registro? Esta ação não poderá ser reverdida.");

        if (resposta == JOptionPane.YES_OPTION) {
            aluno.setIdAluno(Integer.parseInt(this.Jt_ConsulAluno.getText()));
            alun.deletar(aluno);
        }


    }//GEN-LAST:event_jButton5ActionPerformed

