    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed

        Secretaria sec = new Secretaria();
        SecretariaDao secd = new SecretariaDao();

        sec.setNome_secretaria(this.Jt_nome_secretario.getText());
        sec.setSobrenome_secretaria(this.Jt_sobrenome_secretaria.getText());
        sec.setRg_secretaria(Integer.parseInt(this.Jt_rg_secretario.getText()));
        sec.setCpf_secretaria(Double.parseDouble(this.Jt_cpf_secretario.getText()));
        sec.setEndereco_secretaria(this.Jt_endereco_secretario.getText());
        sec.setTelefone_secretaria(Double.parseDouble(this.Jt_telefone_secretario.getText()));
        sec.setEmail_secretaria(this.Jt_email_secretario.getText());
        sec.setNascimento_secretaria(new java.sql.Date(Nascimento.getDate().getTime()));
        sec.setCadastro_secretaria(new java.sql.Date(cadastro.getDate().getTime()));

        int r = JOptionPane.showConfirmDialog(null, "Você tem certeza que quer alterar este registro?");
        if (r == JOptionPane.YES_OPTION) {
            sec.setIdSecretaria(Integer.parseInt(this.Jt_id_secretario.getText()));
            secd.alterar(sec);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        int r = JOptionPane.showConfirmDialog(null, "Você tem certeza que deseja voltar a pagina inicial?");
        if (r == JOptionPane.YES_OPTION) {
            PaginaInicial pi = new PaginaInicial();
            pi.setVisible(true);
            dispose();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

