    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        Secretaria sex = new Secretaria();
        SecretariaDao sec = new SecretariaDao();

        int resposta;
        resposta = JOptionPane.showConfirmDialog(null, "Você tem certeza que quer apagar este Registro? Esta ação não poderá ser reverdida.");

        if (resposta == JOptionPane.YES_OPTION) {
            sex.setIdSecretaria(Integer.parseInt(this.Jt_id_secretario.getText()));
            sec.deletar(sex);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

