    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        Secretaria sec = new Secretaria();
        SecretariaDao dsec = new SecretariaDao();

        sec.setIdSecretaria(Integer.parseInt(this.Jt_id_secretario.getText()));
        sec = dsec.consultar(sec);

        Jt_nome_secretario.setText(sec.getNome_secretaria());
        Jt_sobrenome_secretaria.setText(sec.getSobrenome_secretaria());
        Jt_rg_secretario.setInt(sec.getRg_secretaria());
        Jt_cpf_secretario.setDouble(sec.getCpf_secretaria());
        Jt_endereco_secretario.setText(sec.getEndereco_secretaria());
        Jt_telefone_secretario.setDouble(sec.getTelefone_secretaria());
        Jt_email_secretario.setText(sec.getEmail_secretaria());
        Nascimento.setDate(sec.getNascimento_secretaria());
        cadastro.setDate(sec.getCadastro_secretaria());

        dsec.consultar(sec);
    }//GEN-LAST:event_jButton5ActionPerformed

